const Button = ({ btnText, textColor, width, height, background, className }) => {
    // Concatenate user-defined classes with default Tailwind CSS classes
    const buttonClasses = `py-4 px-7 rounded-lg ${textColor} ${className}`;
  
    return (
      <button
        className={buttonClasses}
        style={{ width: width, height: height, background: background }}
      >
        {btnText}
      </button>
    );
  };
  
  export default Button;
